import React from "react";
import RecipeResult from "./RecipeResult";
import {EDAMAM_API_KEY, EDAMAM_APP_ID} from "../common/constants";



export default class SearchResultsComponent extends React.Component {

  createCORSRequest(method, url) {
    let xhr = new XMLHttpRequest();
    if ("withCredentials" in xhr) {
      // XHR for Chrome/Firefox/Opera/Safari.
      xhr.open(method, url, true);
    }
    else {
      // CORS not supported.
      xhr = null;
    }
    return xhr;
  }

  // Make the actual CORS request.
  makeCorsRequest(queryText) {

    const url = "https://api.edamam.com/search?q=" + queryText + "&app_id="
        + EDAMAM_APP_ID + '&app_key=' + EDAMAM_API_KEY;

    const xhr = this.createCORSRequest('GET', url);
    if (!xhr) {
      alert('CORS not supported');
      return;
    }

    // Response handlers.
    xhr.onload = function () {
      const text = xhr.responseText;
      this.displayResults(JSON.parse(text));
      console.table('results:', JSON.parse(text));
    };

    xhr.onerror = function () {
      alert('Woops, there was an error making the request.');
    };


    xhr.setRequestHeader('Content-Type', 'application/json');
    xhr.send(queryText);
  }


  displayResults(jsonResponse) {
    this.setState({
      results: jsonResponse.hits,
      count: jsonResponse.count
    });

  }

  componentDidMount() {
    this.makeCorsRequest(this.props.queryText);
  }


  render() {
    this.count = 0;

    return (
        <div>
          <h2>Search term: {this.props.queryText}</h2>
          <p>{this.state.count} results found.</p>

            <ul className="nav nav-tabs">
            {this.state.results &&
              this.state.results.map(hit => (
                  <RecipeResult
                      key={this.count++}
                      recipeData={hit}
                  />
              ))}
          </ul>
        </div>
    );
  }
}
