import React from 'react'
// import SearchResultsComponent from '../components/SearchResultsComponent';



class SearchContainer extends React.Component {

  sendQuery(e) {
    e.preventDefault();
    console.log('e', e);
    this.props.history.push(`queryResults/`);
  }
  handleSearchTermSubmit(event) {
    event.preventDefault();
    this.props.onSearchTermSubmit(this.textInput.value);
  }

  state = {
    results: ''
  };

  render() {
    return (
        <div>
          <form onSubmit={this.sendQuery}>

          <input type="text" ref={(input) => this.textInput = input} />
          <button>Search</button>
          <button type="button">Random (doesn't do anything)</button>

          <input type="text" id="searchInput" placeholder="Search for recipes..." />
          <button type="submit">Submit</button>
        </form>
          <div id="queryTerm"></div>
          <div id="numResults"></div>
          <div id="results"></div>


          </div>
    );
  }

}

export default SearchContainer;
