import React from 'react'
import {EDAMAM_APP_ID, EDAMAM_API_KEY} from '../common/constants';
import SearchResultsComponent from '../components/SearchResultsComponent';

//
// function testFunct(e) {
//   e.preventDefault();
//   console.log('e is:', e);
// }



class SearchContainer extends React.Component {

  componentDidMount() {
    this.results = '';
  }

  // Create the XHR object.
  createCORSRequest(method, url) {
    let xhr = new XMLHttpRequest();
    if ("withCredentials" in xhr) {
      // XHR for Chrome/Firefox/Opera/Safari.
      xhr.open(method, url, true);
    }
    else {
      // CORS not supported.
      xhr = null;
    }
    return xhr;
  }

// Make the actual CORS request.
  makeCorsRequest(e) {
    e.preventDefault();

    let searchInput = document.getElementById('searchInput').value;
    let results = document.getElementById('results');

    const url = "https://api.edamam.com/search?q=" + searchInput + "&app_id=" + EDAMAM_APP_ID + '&app_key=' + EDAMAM_API_KEY;

    const xhr = this.createCORSRequest('POST', url);
    if (!xhr) {
      alert('CORS not supported');
      return;
    }

    // Response handlers.
    xhr.onload = function() {
      const text = xhr.responseText;
      this.displayResults(JSON.parse(text));
    };

    xhr.onerror = function() {
      alert('Woops, there was an error making the request.');
    };

    results.innerHTML = 'Loading...';
    xhr.setRequestHeader('Content-Type', 'application/json');
    xhr.send(searchInput);
  }

  displayResults(jsonResponse) {
    const queryTerm = document.getElementById("queryTerm");
    const numResults = document.getElementById("numResults");

    const count = jsonResponse.count;
    const term = jsonResponse.q;
    queryTerm.innerHTML = 'Search results for: ' + term;
    numResults.innerHTML = count + 'results' + count > 1 ? 's' : '';
    this.results = jsonResponse["hits"];
  }

  render() {
    return (
        <div>
        <h1>Recipe Search API</h1>
        <form onSubmit={(e) => this.makeCorsRequest(e)}>
          <input type="text" id="searchInput" placeholder="Search for recipes..." />
          <button type="submit">Submit</button>
        </form>
          <div id="queryTerm"></div>
          <div id="numResults"></div>
          <div id="results"></div>

          {this.results && <SearchResultsComponent  hits={this.results} />}
        </div>
    );
  }

}

export default SearchContainer;
