import React from 'react'
import {EDAMAM_APP_ID, EDAMAM_API_KEY, SAMPLE_RESPONSE} from '../common/constants';
import SearchResultsComponent from '../components/SearchResultsComponent';

import { BrowserRouter as Router, Route } from "react-router-dom";


let results = '';


// Create the XHR object.
function createCORSRequest(method, url) {
  let xhr = new XMLHttpRequest();
  if ("withCredentials" in xhr) {
    // XHR for Chrome/Firefox/Opera/Safari.
    xhr.open(method, url, true);
  }
  else {
    // CORS not supported.
    xhr = null;
  }
  return xhr;
}

// Make the actual CORS request.
function makeCorsRequest(e) {
  e.preventDefault();

  let searchInput = document.getElementById('searchInput').value;
  let resultsText = document.getElementById('results');

  const url = "https://api.edamam.com/search?q=" + searchInput + "&app_id="
      + EDAMAM_APP_ID + '&app_key=' + EDAMAM_API_KEY;

  const xhr = createCORSRequest('GET', url);
  if (!xhr) {
    alert('CORS not supported');
    return;
  }

  // Response handlers.
  xhr.onload = function () {
    const text = xhr.responseText;
    displayResults(JSON.parse(text));
    console.table('results:', JSON.parse(text));
  };

  xhr.onerror = function () {
    alert('Woops, there was an error making the request.');
  };


  resultsText.innerHTML = 'Loading...';
  xhr.setRequestHeader('Content-Type', 'application/json');
  xhr.send(searchInput);
}


function displayResults(jsonResponse) {
  this.setState({results: jsonResponse["hits"]});

  const queryTerm = document.getElementById("queryTerm");
  const numResults = document.getElementById("numResults");

  const count = jsonResponse.count;
  const term = jsonResponse.q;
  queryTerm.innerHTML = 'Search results for: ' + term;
  numResults.innerHTML = count + 'results' + count > 1 ? 's' : '';
}

class SearchContainer extends React.Component {

  state = {
    results: ''
  };

  render() {
    return (
        <div>
        <h1>Recipe Search API</h1>
        <form onSubmit={(e) => this.props.history.push(`queryResults/${searchInput.value}`)} >
          <input type="text" id="searchInput" placeholder="Search for recipes..." />
          <button type="submit">Submit</button>
        </form>
          <div id="queryTerm"></div>
          <div id="numResults"></div>
          <div id="results"></div>

          <Router>
            <Route
                path="/queryResults:queryText"
                exact={true}
                render={props => (
                    <SearchResultsComponent
                        {...props}
                        hits={this.state.results}
                        queryText={props.match.params.queryText}
                    />
                )}
            />
          </Router>
          </div>
    );
  }

}

export default SearchContainer;
