import React from 'react'
import {EDAMAM_APP_ID, EDAMAM_API_KEY} from '../common/constants';

// Application ID
// 8c601d6b
//
// This is the application ID, you should send with each API request.
//
//     Application Keys
// 0832f33a78fc50e2af9f4f2a03f6a0e2	—
// These are application keys used to authenticate requests.

// Create the XHR object.
function createCORSRequest(method, url) {
  let xhr = new XMLHttpRequest();
  if ("withCredentials" in xhr) {
    // XHR for Chrome/Firefox/Opera/Safari.
    xhr.open(method, url, true);
  }
  // else if (typeof XDomainRequest != "undefined") {
  //   // XDomainRequest for IE.
  //   xhr = new XDomainRequest();
  //   xhr.open(method, url);
  // }
  else {
    // CORS not supported.
    xhr = null;
  }
  return xhr;
}

// Make the actual CORS request.
function makeCorsRequest() {
  let recipe = document.getElementById('recipe').value;
  let pre = document.getElementById('response');

  const url = 'https://api.edamam.com/api/nutrition-details?app_id=' + EDAMAM_APP_ID + '&app_key=' + EDAMAM_API_KEY;

  const xhr = createCORSRequest('POST', url);
  if (!xhr) {
    alert('CORS not supported');
    return;
  }

  // Response handlers.
  xhr.onload = function() {
    const text = xhr.responseText;
    pre.innerHTML = text;
  };

  xhr.onerror = function() {
    alert('Woops, there was an error making the request.');
  };

  pre.innerHTML = 'Loading...';
  xhr.setRequestHeader('Content-Type', 'application/json');
  xhr.send(recipe);
}

class SearchContainer extends React.Component {

  render() {
    return (
        <h1>Recipe Search API</h1>
    //       <label htmlFor="app_key">app_key:</label>
    //       <input type="text" name="app_key" id="app_key">
    //       <br>
    // <textarea id="recipe" rows="20" cols="80">
    );
  }

}

export default SearchContainer;
